$(document).ready(function(){
    $("#playcard").css("height",$("#playcard").width()*0.5625+"px");
    $("#player").css("height",$("#playcard").height()+"px");
    $("#comments").css("height",$("#playcard").height()+"px");
    $(window).resize(function(){
        $("#playcard").css("height",$("#playcard").width()*0.5625+"px");
        $("#player").css("height",$("#playcard").height()+"px");
        $("#comments").css("height",$("#playcard").height()+"px");
    });
});